/*global google*/
import React from "react";
import { GoogleMap, InfoWindow, LoadScript, Marker, InfoBox, Polyline} from '@react-google-maps/api';

export default function InfoMap(props){
    // console.log(props);
    // console.log(props.lineGreen);
    // console.log(props.lineblue);
    // console.log(props.lineOrange);
    // console.log(props.lineGray);
    // console.log(props.mapData);
    // console.log(props.popData);
    // console.log(props.trunk);
    // console.log(props.bandWidth);

    /* Data 추출 */
    const line1 = [];
    const line2 = [];
    const line3 = [];
    const line4 = [];
    const line5 = [];
    const line6 = [];
    const line7 = [];
    const line8 = [];

    props.mapData.map(value=>{
        if(value.bw === 300 && value.line === 1){
            return line1.push(value);
        }else if(value.bw === 200 && value.line === 2){
            return line2.push(value);
        }else if(value.bw === 100 && value.line === 3){
            return line3.push(value);
        }else if(value.bw === 100 && value.line === 4){
            return line4.push(value);
        }else if(value.bw === 100 && value.line === 5){
            return line5.push(value);
        }else if(value.bw === 100 && value.line === 6){
            return line6.push(value);
        }else if(value.bw === 100 && value.line === 7){
            return line7.push(value);
        }else if(value.bw === 100 && value.line === 8){
            return line8.push(value);
        }
    })
    
    props.popData.map(value=>{
        if(value.bw === 300 && value.line === 1){
            return line1.push(value);
        }else if(value.bw === 200 && value.line === 2){
            return line2.push(value);
        }else if(value.bw === 100 && value.line === 3){
            return line3.push(value);
        }else if(value.bw === 100 && value.line === 4){
            return line4.push(value);
        }else if(value.bw === 100 && value.line === 5){
            return line5.push(value);
        }else if(value.bw === 100 && value.line === 6){
            return line6.push(value);
        }else if(value.bw === 100 && value.line === 7){
            return line7.push(value);
        }else if(value.bw === 100 && value.line === 8){
            return line8.push(value);
        }
    })
    props.trunk.map(value=>{
        if(value.bw === 300 && value.line === 1){
            return line1.push(value);
        }else if(value.bw === 200 && value.line === 2){
            return line2.push(value);
        }else if(value.bw === 100 && value.line === 3){
            return line3.push(value);
        }else if(value.bw === 100 && value.line === 4){
            return line4.push(value);
        }else if(value.bw === 100 && value.line === 5){
            return line5.push(value);
        }else if(value.bw === 100 && value.line === 6){
            return line6.push(value);
        }else if(value.bw === 100 && value.line === 7){
            return line7.push(value);
        }else if(value.bw === 100 && value.line === 8){
            return line8.push(value);
        }
    })

    console.log(line1);
    console.log(line2);
    console.log(line3);
    console.log(line4);
    console.log(line5);
    console.log(line6);
    console.log(line7);
    console.log(line8);

    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : -4.39636,
        lng : 15.31693
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options = {
        strokeColor:'green',
        strokeOpacity: 0.75,
        strokeWeight: 5,
    };

    /* Polyline options2 */
    const options2 = {
        strokeColor: 'blue',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };
    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        props.mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '5.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>mapData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '6.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>popData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.trunk.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '7.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>Trunk</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }

                    {
                        line1.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line1}
                                options={{
                                    strokeColor:'blue',
                                    strokeOpacity: 0.75,
                                    strokeWeight: 5,
                                }}
                            />
                            );
                        })
                    }
                    {
                        line2.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line2}
                                options={{
                                    strokeColor:'orange',
                                    strokeOpacity: 0.75,
                                    strokeWeight: 5,
                                }}
                            />
                            );
                        })
                    }
                    {
                        line3.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line3}
                                options={{
                                    strokeColor:'orange',
                                    strokeOpacity: 0.75,
                                    strokeWeight: 5,
                                }}
                            />
                            );
                        })
                    }
                    {
                        line4.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line4}
                                options={{
                                    strokeColor:'orange',
                                    strokeOpacity: 0.75,
                                    strokeWeight: 5,
                                }}
                            />
                            );
                        })
                    }
                    {
                        line5.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line5}
                                options={{
                                    strokeColor:'orange',
                                    strokeOpacity: 0.75,
                                    strokeWeight: 5,
                                }}
                            />
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
        </>
    );
}
