/*global google*/
import React from "react";
import { GoogleMap, InfoWindow, LoadScript, Marker, InfoBox, Polyline} from '@react-google-maps/api';

export default function InfoMap(props){
    // console.log(props);
    // console.log(props.lineGreen);
    // console.log(props.lineblue);
    // console.log(props.lineOrange);
    // console.log(props.lineGray);
    // console.log(props.mapData);
    // console.log(props.popData);
    // console.log(props.trunk);
    // console.log(props.bandWidth);
    
    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : -4.39636,
        lng : 15.31693
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };

    /* Polyline options2 */
    const options2 = {
        strokeColor: 'blue',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };
    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        props.mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '2.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>mapData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : 'pop.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>popData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.trunk.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '3.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>Trunk</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    
                    {
                        props.lineGreen.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineGreen}
                                        options={
                                            {
                                                strokeColor:'green',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineblue.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineblue}
                                        options={
                                            {
                                                strokeColor:'blue',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineOrange.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineOrange}
                                        options={
                                            {
                                                strokeColor:'red',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineGray.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineGray}
                                        options={
                                            {
                                                strokeColor:'red',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.linePink.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.linePink}
                                        options={
                                            {
                                                strokeColor:'red',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineYellow.map(()=>{
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineYellow}
                                        options={
                                            {
                                                strokeColor:'red',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 5,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
        </>
    );
}
