import React, { Component } from 'react';
import axios from 'axios';
import Tabledata from './Tabledata';
class Wbnx5 extends Component {
    state={
        data : [],
        result: [],
    };

    getdata = async () =>{
        console.log("data start!");
        const dataset = await axios.get('http://somweb.cf:8000/wbn-x2-ap2')
        .then((data) => {
            console.log('Check WBN-x2-ap2!');
            const mydata = data.data.data;
            console.log(mydata);
            return this.setState({data:mydata});
        }).catch(()=>{
            alert('ERROR MESSAGE!');
            console.log('ERROR MESSAGE!');
        })
    }
    gettimer = () =>{
        console.log("Time Start!");

        let time = new Date();
        let years = time.getFullYear();
        let month = time.getMonth();
        let date = time.getDate();
        let hours = time.getHours();
        let minutes = time.getMinutes();
        let seconds = time.getSeconds();

        const result = `Time Stamp : ${years}-${month+1}-${date}/${hours}:${minutes}:${seconds}`;

        return this.setState({result:result});
    }
    componentDidMount(){
        this.getdata();
        this.gettimer();
    }
    renderbtn = () => {
        this.getdata();
        this.gettimer();
        alert('데이터를 새로고침하였습니다.');
    }
    render() {
        const {data, result} = this.state;
        return (
            <div className="table">
                {isLoading ? <div className="loader">
                    <span className="loader__text">{errmessage}</span>
                </div> :
                    ( 
                        <div className="table_1">
                            <h1> Network Node : WBN_X2(AP4)</h1>
                            <span className="spantitle">
                                <button onClick={this.renderbtn}>불러오기</button>
                                <p>{result}</p>
                            </span>
                            <table>
                                <tbody className="table_1_size">
                                    <tr>
                                        <td>Offset Peridod: 5 Mins</td>
                                        <td colSpan="3">BandWidth(Mbps)</td>
                                    </tr>
                                    <tr>
                                        <td>offset</td>
                                        <td>IN</td>
                                        <td>Out</td>
                                        <td>Total</td>
                                    </tr>
                                    {
                                        data.map(data => (
                                            <Tabledata 
                                                offset={data.offset}
                                                speed_in={data.speed_in}
                                                speed_out={data.speed_out}
                                                speed_total={data.speed_total}
                                            />
                                        ))
                                    }
                                </tbody>
                            </table>
                        </div>
                    )
                }
            </div>
        );
    }
}

export default Wbnx5;
