/*global google*/
import React,{ useState } from 'react';
import { LoadScript, GoogleMap, Marker, InfoWindow, Polyline } from '@react-google-maps/api';
export default function Testmap(props) {
    //console.log(props.totalLine);

    const [activeMarker, setActiveMarker] = useState(null);

    const center = {
        lat : 37.666169,
        lng  : 128.710769
    };
    const options400 = {
        strokeColor:'yellowgreen',
        strokeOpacity: 0.75,
        strokeWeight: 8,
    };
    const options300 = {
        strokeColor:'gold',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };
    const options200 = {
        strokeColor:'gray',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };
    const options100 = {
        strokeColor:'brown',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };
    let i = 1;
    const xx = props.totalLine.map((value,index)=>{
        i++;
        console.log(index+1);
        switch(value.line){
            case 1: 
                console.log(value.num);
                break;
            case 2 :
                console.log(value.num);
                break;
            case 3 : 
                console.log(value.num);
                break;
            case 4 :
                console.log(value.num);
                break;
            case 5 :
                console.log(value.num);
                break;
            case 6 :
                console.log(value.num);
                break;
            case 7 :
                console.log(value.num);
                break;
            default : 
                console.log(value);
        }
    })

    const bw400 = props.totalLine.filter((value,index,arr)=>{
        //console.log(index);
        //console.log(value.line)
        return value.line === index && value.bw === 400 ? {lat : value.lat , lng : value.lng} : null
    }).map((value,index,arr)=>{
        //console.log(arr);
        return (
            <Polyline
                path={[arr[index],arr[index+1]]}
                options={options400}
            />
        );
    })
    const bw300 = props.totalLine.filter((value,index,arr)=>{
        return value.line === index && value.bw === 300 ? {lat : value.lat , lng : value.lng} : null
    }).map((value,index,arr)=>{
        //console.log(arr);
        return (
            <Polyline
                path={[arr[index],arr[index+1]]}
                options={options300}
            />
        );
    })
    const bw200 = props.totalLine.filter((value,index,arr)=>{
        return value.line === index && value.bw === 200 ? {lat : value.lat , lng : value.lng} : null
    }).map((value,index,arr)=>{
        //console.log(arr);
        return (
            <Polyline
                path={[arr[index],arr[index+1]]}
                options={options200}
            />
        )
    })
    const bw100 = props.totalLine.filter((value,index,arr)=>{
        return value.line === index && value.bw === 100 ? {lat : value.lat , lng : value.lng} : null
    }).map((value,index,arr)=>{
        //console.log(arr);
        return (
            <Polyline
                path={[arr[index],arr[index+1]]}
                options={options100}
            />
        )
    })

    // console.log(bw400)
    // console.log(bw300)
    // console.log(bw200)
    // console.log(bw100)



    const handleActiveMarker = (marker) => {
        if (marker === activeMarker) {
            return;
        }
        setActiveMarker(marker);
    };

    const handleOnLoad = (map) => {
        const bounds = new google.maps.LatLngBounds();
        props.mapData.forEach(({ position }) => bounds.extend(position));
        map.fitBounds(bounds);
    };

    return (
        <div>
            
            <div className="kinds">
                <img className="pop" src="nodepop.png" alt="pop" />
                <p>PoP</p>
                <img className="trunk" src="3.png" alt="trunk" />
                <p>ACS Trunk Node</p>
                <img className="wbn" src="2.png" alt="node"/>
                <p>WBN Node</p>
            </div>
            <div className="bandwidthLevel">
                <img className="linestyle" src="color1.png" alt="bw100" />
                <p>Bronze</p>
                <img className="linestyle" src="color2.png" alt="bw200" />
                <p>Silver</p>
                <img className="linestyle" src="color3.png" alt="bw300" />
                <p>Gold</p>
                <img className="linestyle" src="color4.png" alt="bw400"/>
                <p>Platinum</p>
            </div>
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={{
                        width : '1900px',
                        height : '700px',
                        margin : '20px'
                    }}
                    center={center}
                    zoom={13}
                    onClick={()=>{setActiveMarker(null)}}
                >
                    {
                        props.mapData_s.map((value)=>{
                            //console.log(value);
                            return(
                                <>
                                    <Marker 
                                        position={value}
                                        icon={
                                            {
                                                url : '2.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                        onMouseOver={() => {handleActiveMarker(value.num)}}
                                    />
                                    {activeMarker === value.num && (
                                        <InfoWindow 
                                            position={{lat : value.lat , lng : value.lng}}
                                            onCloseClick={() => setActiveMarker(null)}
                                        >
                                            <div className="infowindow">
                                                <p className="infoitem info_title">{value.wn_node}</p>
                                                <p className="infoitem">lat : {value.lat}</p>
                                                <p className="infoitem">lng : {value.lng}</p>
                                            </div>
                                        </InfoWindow>
                                    )}
                                </>
                            );
                        })
                    }
                    {
                        props.popData_s.map(value=>{
                            //console.log(value);
                            return (
                                <>
                                    <Marker 
                                        position={value}
                                        icon={
                                            {
                                                url : '1.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                        onMouseOver={() => {handleActiveMarker(value.num)}}
                                    />
                                    {activeMarker === value.num && (
                                        <InfoWindow 
                                            position={{lat : value.lat, lng : value.lng}}
                                            onCloseClick={() => setActiveMarker(null)}
                                        >
                                            <div className="infowindow">
                                                <p className="infoitem info_title">{value.p_node}</p>
                                                <p className="infoitem">lat : {value.lat}</p>
                                                <p className="infoitem">lng : {value.lng}</p>
                                            </div>
                                        </InfoWindow>
                                    )}
                                </>
                            );
                        })
                    }
                    {
                        props.trunk_s.map(value=>{
                            //console.log(value);
                            return (
                                <>
                                <Marker 
                                    position={value}
                                    icon={
                                        {
                                            url : '3.png',
                                            scaledSize : new google.maps.Size(20,25),
                                        }
                                    }
                                    onMouseOver={() => {handleActiveMarker(value.num)}}
                                />
                                {activeMarker === value.num && (
                                    <InfoWindow 
                                        position={{lat : value.lat, lng : value.lng}}
                                        onCloseClick={() => setActiveMarker(null)}
                                    >
                                        <div className="infowindow">
                                            <p className="infoitem info_title">{value.an_node}</p>
                                            <p className="infoitem">lat : {value.lat}</p>
                                            <p className="infoitem">lng : {value.lng}</p>
                                        </div>
                                    </InfoWindow>
                                )}
                                </>
                            );
                        })
                    }

                    {bw400}
                    
                    {bw300}

                    {bw200}

                    {bw100}

                </GoogleMap>
            </LoadScript>
        </div>
    )
}
