/*global google*/
import React,{ useState,useEffect } from "react";
import { GoogleMap,InfoWindow,LoadScript, Marker, InfoBox} from '@react-google-maps/api';
import axios from "axios";

function Map(){
    const DocsApiKeyInput= require("./DocsApiKeyInput").default;
    /* 
    (1) Data 가져오기 v
    (2) Google Map 연결 v
    (3) Data에 있는 좌표 추출 v
    (4) 추출한 좌표에 마커를 생성 v
    (5) 마커 사이즈 변경 v
    (6) 마커에 부가정보 넣기
     */
    const [mapData,setMapData] = useState([]);
    //const mapData = [];

    useEffect(() => {
        extraction();
    },[]);

    const style = {
        width : '1900px',
        height : '700px'
    };
    const center = {
        lat : 37.55480,
        lng : 126.98297
    };
    /*const icon = {
        url : 'O2I_blue.png',
        scaledSize : new google.maps.Size(20,20),
    };*/
    const extraction = async () =>{
        await axios.get('http://somweb.cf:8000/wn-site')
        .then(value=>{
            //console.log('extraction');
            setMapData(value.data.data);
        })
    };
    const canvas = {
        background : '#fff',
        padding: 10
    };
    const onClickbtn = () =>{
        extraction();
        //console.log("Start");
    }
    /*const mouseEvent = (value) =>{
        console.log(value);
            <InfoWindow
                position={{lat : 37.560807 , lng : 126.973924}}
            >
                <div style={canvas}>
                    <h1>{value.id}</h1>
                </div>
            </InfoWindow>
    }*/
    //console.log(mapData);
    
    const options = { closeBoxURL: '', enableEventPropagation: true };
    

    return (
        <div className="map">
            <DocsApiKeyInput/>
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={10}
                >
                    {
                        mapData.map((value)=>{
                            console.log(value.lat, value.lng);
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}} 
                                        icon={
                                            {
                                                url : 'O2I_blue.png',
                                                scaledSize : new google.maps.Size(15,15),
                                            }
                                        }
                                        //onMouseOver={mouseEvent}
                                        onMouseOver={(e)=>{
                                            console.log(e);
                                        }}
                                    />
                                    <InfoBox
                                        options={options}
                                        position={center}
                                    >
                                    <div style={{ backgroundColor: 'yellow', opacity: 0.75, padding: 12 }}>
                                        <div style={{ fontSize: 16, fontColor: `#08233B` }}>
                                        Hello, World!
                                        </div>
                                    </div>
                                    </InfoBox>
                                    {/* {<InfoWindow
                                        position={{lat : value.lat , lng : value.lng}}
                                    >
                                        <div style={canvas}>
                                            <h1>{value.nw_node}</h1>
                                        </div>
                                    </InfoWindow>} */}
                                </>
                            )
                        })
                    }
                </GoogleMap>
            </LoadScript>
            <button onClick={onClickbtn}>Update</button>
        </div>
    );
}

export default Map;
