        return {lat : value.lat,lng : value.lng};
    });
    const line5 = props.line5.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line6 = props.line6.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line7 = props.line7.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line8 = props.line8.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line9 = props.line9.map((value)=>{
        return {lat : value.lat,lng : value.lng};
