import React from 'react';
import { GoogleMap, LoadScript, Marker, Polyline } from '@react-google-maps/api';

export default function Googlemap({totalLine}) {
    
    const google = window.google;
    const center = { 
        lat : 37.672481, 
        lng : 128.736743
    };
    const zoom = 14;
    const mapSize = {
        width : '1900px',
        height : '700px',
        margin : '20px'
    };
    
    
    const ifmarker = totalLine.map(value=>{
        if(value.wn_node){
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "battery25.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
                />
            )
        }else if(value.p_node){
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "nodepop.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
            />
            )
        }else {
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "3.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
                />
            )
        }
    });

    const polyLine = totalLine.sort((a,b)=>{
        return a.line - b.line;
    });
    
    const options400 = {
        strokeColor:'yellowgreen',
        strokeOpacity: 0.75,
        strokeWeight: 8,
    };
    const options300 = {
        strokeColor:'gold',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };
    const options200 = {
        strokeColor:'gray',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };
    const options100 = {
        strokeColor:'brown',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };

    const bw400 = [];
    const bw300 = [];
    const bw200 = [];
    const bw100 = [];

    const bandWidth = polyLine.map((value,index,arr)=>{
        if(value.bw === 400){
            bw400.push(value);
        }else if(value.bw === 300){
            bw300.push(value);
        }else if(value.bw === 200){
            bw200.push(value);
        }else if(value.bw === 100){
            bw100.push(value);
        }else {
            console.log('Err');
        }
    });

    console.log(bw400);
    console.log(bw300);
    console.log(bw200);
    console.log(bw100);

    return (
        <div>
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap center={center} zoom={zoom} mapContainerStyle={mapSize}>
                    {totalLine && ifmarker}
                </GoogleMap>
            </LoadScript>
        </div>
    )
}
