/*global google*/
import React,{ useState,useEffect } from "react";
import { GoogleMap,InfoWindow,LoadScript, Marker} from '@react-google-maps/api';
import axios from "axios";

function Map(){
    /* 
    (1) Data 가져오기 v
    (2) Google Map 연결 v
    (3) Data에 있는 좌표 추출 v
    (4) 추출한 좌표에 마커를 생성 v
    (5) 마커 사이즈 변경
    (6) 마커에 부가정보 넣기
     */
    const [mapData,setMapData] = useState([]);
    //const mapData = [];

    useEffect(() => {
        extraction();
    },[]);
    const style = {
        width : '1900px',
        height : '700px'
    };
    const center = {
        lat : 37.55480,
        lng : 126.98297
    };
    const icon = {
        url : 'O2I_blue.png',
        scale : 2,
    };
    const extraction = async () =>{
        await axios.get('http://somweb.cf:8000/wn-site')
        .then(value=>{
            //console.log('extraction');
            setMapData(value.data.data);
        })
    };
    const canvas = {
        background : '#fff',
        border : '1px solid #222',
        padding: 10
    };
    const onClickbtn = () =>{
        extraction();
        //console.log("Start");
    }
    const hoverEvent = (value) =>{
        return (
            <InfoWindow
                position={{lat : value.lat , lng : value.lng}}
            >
                <div style={canvas}>
                    <h1>{value.id}</h1>
                </div>
            </InfoWindow>
        );
    }
    //console.log(mapData);

    return (
        <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}} 
                                        icon={icon}
                                        onClick={hoverEvent}
                                    />
                                </>
                            )
                        })
                    }
                </GoogleMap>
            </LoadScript>
            <button onClick={onClickbtn}>Update</button>
        </div>
    );
}

export default Map;
