import React, { Component } from 'react';
import axios from 'axios';
import { Line, Bar } from 'react-chartjs-2';

class Wbnx5 extends Component {
    state={
        data : [],
        result: [],
        isLoading: true,
        errcode: 'Loading...',
        timestamp: [],
    };

    getdata = async () =>{
        console.log("Requesting Data!");
        const dataset = await axios.get('http://somweb.cf:8000/wn-t1-x1-e0')
        .then((data) => {
            console.log('Data Check!');
            const mydata = data.data.data;
            return this.setState({data:mydata, isLoading: false});
        }).catch(()=>{
            this.setState({errcode:'NOT FOUND FILE!'});
        })
    }
    gettimer = () =>{
        console.log("Time Start!");

        let time = new Date();
        let years = time.getFullYear();
        let month = time.getMonth();
        let date = time.getDate();
        let hours = time.getHours();
        let minutes = time.getMinutes();
        let seconds = time.getSeconds();

        const result = `Requested Time : ${hours}:${minutes}:${seconds}, ${years}-${month+1}-${date}`;

        return this.setState({result:result});
    }
    getstamp = () =>{
        axios.get('http://somweb.cf:8000/wbn-timestamp').then(response=>{
            const x = response.data.data;
            console.log('최신 데이터 시간을 업데이트하였습니다.');
            return this.setState({timestamp : x});
        }).catch(()=>{
            this.setState({errcode:'NOT FOUND FILE!'});
        })
    }
    componentDidMount(){
        this.getdata();
        this.gettimer();
        this.getstamp();
    }
    renderbtn = () => {
        this.getdata();
        this.gettimer();
        this.getstamp();
    }
    render() {
        const {data, result, isLoading, errcode, timestamp} = this.state;
        const tt = timestamp.map(data=>{
            const y = data.timestamp;
            return (<p className="xx">Last Updated at {y} (offset: 5 mins)</p>);
        });
        
        const data12 = data.filter((value)=>{
            return value.offset < 13;
        });
        const data24 = data.filter((value)=> {
            return value.offset >= 13;
        });

        const chartall = data.filter((value)=>{
            return value.offset;
        })
        const chartoffset = chartall.map((value)=>{
            return value.offset;
        })

        const chartspeed_in = data.map((value)=>{
            return value.speed_in;
        });
        const chartspeed_out = data.map((value)=>{
            return value.speed_out;
        });
        const chartspeed_total = data.map((value)=>{
            return value.speed_total;
        });
        console.log(chartspeed_total);
        /* change */

        let min = 1;
        let max = 0;

        const minValue = function totalValue(x){
            if(min > x){
                min = x;
            }else if(max < x){
                max = x;
            }
        };
        const chartdataset = chartspeed_total.map(minValue);
        console.log(min,max);

        let chaptertotal = [];
        let chapter1 = [];
        let chapter2 = [];
        let chapter3 = [];
        let chapter4 = [];
        let chapter5 = [];

        const Linedata = chartspeed_total.map((value)=>{
            if(min <= value && min+2 > value){
                chapter1.push({value : value});
                chaptertotal.push({value : value});
            }else if(min+2 <= value && min+5 > value){
                chapter2.push(value);
                chaptertotal.push({value : value});
            }else if(min+5 <= value && min+7 > value){
                chapter3.push(value);
                chaptertotal.push({value : value});
            }else if(min+7 <= value && min+10 > value){
                chapter4.push(value);
                chaptertotal.push({value : value});
            }else if(min+10 <= value && min+12 > value){
                chapter5.push(value);
                chaptertotal.push({value : value});
            }else if(min+12<= value && max > value){
                chapter5.push(value);
                chaptertotal.push({value : value});
            }else{
                console.log(Error);
            }
        });
        
        console.log(chapter1);
        console.log(chapter2);
        console.log(chapter3);
        console.log(chapter4);
        console.log(chapter5);

        const totalLine = [];

        chaptertotal.push({length : chapter1.length});
        chaptertotal.push({length : chapter2.length});
        chaptertotal.push({length : chapter3.length});
        chaptertotal.push({length : chapter4.length});
        chaptertotal.push({length : chapter5.length});
        
        let offset = [];

        function xLine(){
            for(let i = 0; i <= totalLine.length; i+=2){
            offset[i] = `${min+i} ~ ${min+i+1}`;
            }
            return chaptertotal.push({xname : offset});
        }

        const lineall = [];
        lineall.push({length : chapter1.length});
        lineall.push({length : chapter2.length});
        lineall.push({length : chapter3.length});
        lineall.push({length : chapter4.length});
        lineall.push({length : chapter5.length});
        console.log(lineall);
        const lineall2 = lineall.map(value=>{
            return value.length;
        })

        xLine();

        console.log(chaptertotal);
        console.log(totalLine);

        /*finish */

        const linedata = {
            labels:chartoffset,
            datasets: [
                {
                    label:'IN',
                    data: lineall2,
                    fill: true,
                    backgroundColor:'rgba(75,192,192)',
                    borderColor:'rgba(75,192,192)'
                },
                {
                    label:'Out',
                    data: chartspeed_out,
                    backgroundColor : '#742774',
                    borderColor: '#742774',
                }
            ]
        };
        return (
            <div className="table">
                {isLoading ? <div className="loader">
                    <span className="loader__text">{errcode}</span>
                </div> :
                    ( 
                        <div className="container">
                            <span className="spantitle">
                            <div className="titletotal">
                                <span className="title1 pstyle">Network Node Name:</span>
                                <span className="title2 pstyle">WN-T1-X1(R1-AP3)</span>
                            </div>
                            <div className="itembox">
                                <p className="result">{result}</p>
                                <button onClick={this.renderbtn}>Next</button>
                            </div>
                            {tt}
                        </span>

                        <div className="content">
                            <div className="chart">
                                <p>BandWidth Analysis</p>
                                <Bar 
                                    data={linedata}
                                    width={600}
                                    height={500}
                                    options={
                                        {
                                            scales:{
                                                x : {
                                                    title: {
                                                        display: true,
                                                        align: 'center',
                                                        font: {
                                                            size: 15,
                                                        },
                                                        text : 'Time offset'
                                                    }
                                                },
                                                y : {
                                                    title: {
                                                        display: true,
                                                        align: 'end',
                                                        font: {
                                                            size: 15,
                                                        },
                                                        text: 'Mbps'
                                                    }
                                                }
                                            }
                                        }
                                    }
                                />
                            </div>
                            <table className="tableset">
                                <thead className="headstyle">
                                    <tr>
                                        <td>Time</td>
                                        <td colSpan="3">BandWidth(Mbps)</td>
                                    </tr>
                                    <tr>
                                        <td>offset</td>
                                        <td>In</td>
                                        <td>Out</td>
                                        <td>Total</td>
                                    </tr>
                                </thead>
                                <tbody className='size'>
                                    {
                                        data12.map(value=>{
                                            return (
                                                <tr>
                                                    <td>{value.offset}</td>
                                                    <td>{value.speed_in}</td>
                                                    <td>{value.speed_out}</td>
                                                    <td>{value.speed_total}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>

                            <table className="tableset">
                                <thead className="headstyle">
                                    <tr>
                                        <td>Time</td>
                                        <td colSpan="3">BandWidth(Mbps)</td>
                                    </tr>
                                    <tr>
                                        <td>offset</td>
                                        <td>In</td>
                                        <td>Out</td>
                                        <td>Total</td>
                                    </tr>
                                </thead>
                                <tbody className='size'>
                                    {
                                        data24.map(value =>{
                                            return (
                                                <tr>
                                                    <td>{value.offset}</td>
                                                    <td>{value.speed_in}</td>
                                                    <td>{value.speed_out}</td>
                                                    <td>{value.speed_total}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>

                        </div>
                    </div>
                    )
                }
            </div>
        );
    }
}

