/*global google*/
import React from "react";
import { GoogleMap, InfoWindow, LoadScript, Marker, InfoBox, Polyline} from '@react-google-maps/api';

export default function InfoMap(props){
    // console.log(props);
    // console.log(props.lineGreen);
    // console.log(props.lineblue);
    // console.log(props.lineOrange);
    // console.log(props.lineGray);
    // console.log(props.mapData);
    // console.log(props.popData);
    // console.log(props.trunk);
    // console.log(props.bandWidth);

    /* Data 추출 */
    const line1 = [];
    const line2 = [];
    const line3 = [];
    const line4 = [];
    const line5 = [];
    const line6 = [];
    const line7 = [];
    const line8 = [];
    const line9 = [];
    const line10 = [];
    const line11 = [];
    const line12 = [];
    const line13 = [];
    const line14 = [];
    const line15 = [];

    props.mapData.map(value=>{
        if(value.line === 1 && 0 <= value.bw && value.bw <= 100){
            line1.push(value);
        }else if(value.line === 1 && 100 < value.bw && value.bw <= 200){
            line2.push(value);
        }else if(value.line === 1 && 200 < value.bw && value.bw <= 300){
            line3.push(value);
        }else if(value.line === 2 && 0 <= value.bw && value.bw <= 100){
            line4.push(value);
        }else if(value.line === 2 && 100 < value.bw && value.bw <= 200){
            line5.push(value);
        }else if(value.line === 2 && 200 < value.bw && value.bw <= 300){
            line6.push(value);
        }else if(value.line === 3 && 0 <= value.bw && value.bw <= 100){
            line7.push(value);
        }else if(value.line === 3 && 100 < value.bw && value.bw <= 200){
            line8.push(value);
        }else if(value.line === 3 && 200 < value.bw && value.bw <= 300){
            line9.push(value);
        }else if(value.line === 4 && 0 <= value.bw && value.bw <= 100){
            line10.push(value);
        }else if(value.line === 4 && 100 < value.bw && value.bw <= 200){
            line11.push(value);
        }else if(value.line === 4 && 200 < value.bw && value.bw <= 300){
            line12.push(value);
        }else if(value.line === 5 && 0 <= value.bw && value.bw <= 100){
            line13.push(value);
        }else if(value.line === 5 && 100 < value.bw && value.bw <= 200){
            line14.push(value);
        }else if(value.line === 5 && 200 < value.bw && value.bw <= 300){
            line15.push(value);
        }
    })
    
    props.popData.map(value=>{
        if(value.line === 1 && 0 <= value.bw && value.bw <= 100){
            line1.push(value);
        }else if(value.line === 1 && 100 < value.bw && value.bw <= 200){
            line2.push(value);
        }else if(value.line === 1 && 200 < value.bw && value.bw <= 300){
            line3.push(value);
        }else if(value.line === 2 && 0 <= value.bw && value.bw <= 100){
            line4.push(value);
        }else if(value.line === 2 && 100 < value.bw && value.bw <= 200){
            line5.push(value);
        }else if(value.line === 2 && 200 < value.bw && value.bw <= 300){
            line6.push(value);
        }else if(value.line === 3 && 0 <= value.bw && value.bw <= 100){
            line7.push(value);
        }else if(value.line === 3 && 100 < value.bw && value.bw <= 200){
            line8.push(value);
        }else if(value.line === 3 && 200 < value.bw && value.bw <= 300){
            line9.push(value);
        }else if(value.line === 4 && 0 <= value.bw && value.bw <= 100){
            line10.push(value);
        }else if(value.line === 4 && 100 < value.bw && value.bw <= 200){
            line11.push(value);
        }else if(value.line === 4 && 200 < value.bw && value.bw <= 300){
            line12.push(value);
        }else if(value.line === 5 && 0 <= value.bw && value.bw <= 100){
            line13.push(value);
        }else if(value.line === 5 && 100 < value.bw && value.bw <= 200){
            line14.push(value);
        }else if(value.line === 5 && 200 < value.bw && value.bw <= 300){
            line15.push(value);
        }
    })
    props.trunk.map(value=>{
        if(value.bw === 300 && value.line === 1){
            return line1.push(value);
        }else if(value.bw === 200 && value.line === 2){
            return line2.push(value);
        }else if(value.bw === 100 && value.line === 3){
            return line3.push(value);
        }else if(value.bw === 100 && value.line === 4){
            return line4.push(value);
        }else if(value.bw === 100 && value.line === 5){
            return line5.push(value);
        }else if(value.bw === 100 && value.line === 6){
            return line6.push(value);
        }else if(value.bw === 100 && value.line === 7){
            return line7.push(value);
        }else if(value.bw === 100 && value.line === 8){
            return line8.push(value);
        }
    })

    console.log(line1);
    console.log(line2);
    console.log(line3);
    console.log(line4);
    console.log(line5);
    console.log(line6);
    console.log(line7);
    console.log(line8);

    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : -4.39636,
        lng : 15.31693
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const blueoptions = {
        strokeColor:'blue',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };

    const greenoptions = {
        strokeColor:'green',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };

    const orangeoptions = {
        strokeColor:'orange',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };
    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        props.mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '5.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>mapData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '6.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>popData</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }
                    {
                        props.trunk.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '7.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>Trunk</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }

                    {
                        line1.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line1}
                                options={blueoptions}
                            />
                            );
                        })
                    }
                    {
                        line2.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line2}
                                options={greenoptions}
                            />
                            );
                        })
                    }
                    {
                        line3.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line3}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                    {
                        line4.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line4}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                    {
                        line5.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line5}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                    {
                        line6.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line6}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                    {
                        line7.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line7}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                    {
                        line8.map((value)=>{
                            console.log(value);
                            return(
                                <Polyline
                                path={line8}
                                options={orangeoptions}
                            />
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
        </>
    );
}
