import React, { Component } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

class Wbnx1 extends Component {
    state={
        data : [],
        result: [],
        isLoading: true,
        errcode: 'Loading...',
        timestamp: [],
    };

    getdata = async () =>{
        console.log("Requesting Data!");
        await axios.get('http://somweb.cf:8000/wbn-x1-r0')
        .then((data) => {
            console.log('Data Check!');
            const mydata = data.data.data;
            return this.setState({data:mydata, isLoading: false});
        }).catch(()=>{
            alert('SERVER ERROR');
            this.setState({errcode:'NOT FOUND FILE!'});
        })
    }
    gettimer = () =>{
        console.log("Time Start!");

        let time = new Date();
        let years = time.getFullYear();
        let month = time.getMonth();
        let date = time.getDate();
        let hours = time.getHours();
        let minutes = time.getMinutes();
        let seconds = time.getSeconds();

        const result = ` : (${hours}:${minutes}:${seconds}) ${years}-${month+1}-${date}`;

        return this.setState({result:result});
    }
    getstamp = () =>{
        axios.get('http://somweb.cf:8000/wbn-timestamp').then(response=>{
            const x = response.data.data;
            console.log('최신 데이터 시간을 업데이트하였습니다.');
            return this.setState({timestamp : x});
        }).catch(()=>{
            alert('SERVER ERROR');
            this.setState({errcode:'NOT FOUND FILE!'});
        })
    }
    componentDidMount(){
        this.getdata();
        this.gettimer();
        this.getstamp();
    }
    renderbtn = () => {
        this.getdata();
        this.gettimer();
        this.getstamp();
        alert('데이터를 새로고침하였습니다.');
    }
    
    render() {
        const {data, result,isLoading, errcode, timestamp} = this.state;
        const tt = timestamp.map(data=>{
            const y = data.timestamp;
            return (<p>Observed Time : {y}</p>);
        });
        const data12 = data.filter((value)=>{
            return value.offset < 13;
        });
        const data24 = data.filter((value)=> {
            return value.offset >= 13;
        });
        const chart12 = data12.filter(value =>{
            return value.speed_in;
        })
        console.log(data12);
        console.log(chart12);
        console.log(data24);
        const linedata = {
            labels:["1","2","3","4","5","6"],
            datasets: [
                {
                    label:'First dataset',
                    data:{chart12},
                    fill: true,
                    backgroundColor:'rgba(75,192,192,0.2)',
                    borderColor:'rgba(75,192,192,1)'
                },
                {
                    label:'Second dataset',
                    data: [40,60,80,40,30,50],
                    fill:false,
                    borderColor: '#742774',
                }
            ]
        };
        return (
            <div className="table">
                {isLoading ? <div className="loader">
                    <span className="loader__text">{errcode}</span>
                </div> :
                (
                    <div className="container">
                        <div className="spantitle">
                            <h2> Network Node : WBN_X1(R0) </h2>
                            {tt}
                            <div className="itembox">
                                <button onClick={this.renderbtn}>Start Time Measued</button>
                                <p>{result}</p>
                            </div>
                        </div>

                        <div className="content">

                            <table className="tableset">
                                <tbody>
                                    <tr>
                                        <td>Time Offset</td>
                                        <td colSpan="3">BandWidth(Mbps)</td>
                                    </tr>
                                    <tr>
                                        <td>offset</td>
                                        <td>IN</td>
                                        <td>Out</td>
                                        <td>Total</td>
                                    </tr>
                                    {
                                        data12.map(value=>{
                                            return (
                                                <tr>
                                                    <td>{value.offset}</td>
                                                    <td>{value.speed_in}</td>
                                                    <td>{value.speed_out}</td>
                                                    <td>{value.speed_total}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>

                            <table className="tableset">
                                <tbody>
                                    <tr>
                                            <td>Time Offset</td>
                                            <td colSpan="3">BandWidth(Mbps)</td>
                                        </tr>
                                        <tr>
                                            <td>offset</td>
                                            <td>IN</td>
                                            <td>Out</td>
                                            <td>Total</td>
                                        </tr>
                                    {
                                        data24.map(value =>{
                                            return (
                                                <tr>
                                                    <td>{value.offset}</td>
                                                    <td>{value.speed_in}</td>
                                                    <td>{value.speed_out}</td>
                                                    <td>{value.speed_total}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>
                            <div className="chart">
                                <Line 
                                    data={linedata}
                                    width={300}
                                    height={300}
                                
                                />
                            </div>
                        </div>
                    </div>
                )
                }
            </div>
        );
    }
}

export default Wbnx1;
