/*global google*/
import React from "react";
import { GoogleMap, InfoWindow, LoadScript, Marker, InfoBox, Polyline} from '@react-google-maps/api';

export default function InfoMap(props){
    console.log(props);
    console.log(props.lineGreen);
    console.log(props.lineblue);
    console.log(props.lineOrange);
    console.log(props.lineGray);
    console.log(props.mapData);
    console.log(props.popData);
    console.log(props.trunk);
    console.log(props.bandWidth);
    
    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : 37.57055,
        lng : 126.97684
    };

    /* google map icon */
    /*function initMap() {
        const icon = {
            url : 'pop.png',
            scaledSize : new google.maps.Size(20,20),
        };
    }*/

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };

    /* Polyline options2 */
    const options2 = {
        strokeColor: 'blue',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };
    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={15}
                >
                    {
                        props.mapData.map((value)=>{
                            return (
                                <Marker 
                                    position={{lat : value.lat , lng : value.lng}}
                                    icon={
                                        {
                                            url : '2.png',
                                            scaledSize : new google.maps.Size(20,20),
                                        }
                                    }
                                />
                            );
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <Marker 
                                    position={{lat : value.lat , lng : value.lng}}
                                    icon={
                                        {
                                            url : 'pop.png',
                                            scaledSize : new google.maps.Size(20,20),
                                        }
                                    }
                                />
                            );
                        })
                    }
                    {
                        props.trunk.map((value)=>{
                            //console.log(value);
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '3.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {/* <Polyline path={[{ lat: 37.56643, lng: 126.97511}, { lat: 37.560901, lng: 126.973908 }]}/> */}
                    {
                        props.lineGreen.map((value)=>{
                            console.log(value.lat,value.lng);
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineGreen}
                                        options={
                                            {
                                                strokeColor:'green',
                                                strokeOpacity: 0.75,
                                                strokeWeight: 2,
                                            }
                                        }
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineblue.map((value)=>{
                            console.log(value.lat,value.lng);
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineblue}
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineOrange.map((value)=>{
                            console.log(value.lat,value.lng);
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineOrange}
                                    />
                                </>
                            );
                        })
                    }
                    {
                        props.lineGray.map((value)=>{
                            console.log(value.lat,value.lng);
                            return (
                                <>
                                    <Polyline 
                                        path={props.lineGray}
                                    />
                                </>
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
        </>
    );
}
