        return {lat : value.lat,lng : value.lng};
    });
    const line6 = props.line6.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line7 = props.line7.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line8 = props.line8.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line9 = props.line9.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line10 = props.line10.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line11 = props.line11.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line12 = props.line12.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line13 = props.line13.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line14 = props.line14.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line15 = props.line15.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line16 = props.line16.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line17 = props.line17.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line18 = props.line18.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });

    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : -4.39636,
        lng : 15.31693
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options300 = {
        strokeColor:'blue',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };
    const options200 = {
        strokeColor:'green',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };
    const options100 = {
        strokeColor:'orange',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };

    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        props.mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '5.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                        onClick={()=>{setInfoWindowVisible(!infoWindowVisible)}}
                                    />
                                        {infoWindowVisible && (
                                            <InfoWindow position={{lat : value.lat , lng : value.lng}}>
                                                <h1>mapData</h1>
                                            </InfoWindow>
                                        )}
                                </>
                            );
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '6.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {infoWindowVisible && (
                                            <InfoWindow position={{lat : value.lat , lng : value.lng}}>
                                                <h1>popData</h1>
                                            </InfoWindow>
                                        )}
                                </>
                            );
                        })
                    }
                    {
                        props.trunk.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '7.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                    />
                                    {/* {<InfoWindow>
                                        <h1>Trunk</h1>
                                    </InfoWindow>} */}
                                </>
                            );
                        })
                    }

                    {
                        props.line1.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line1} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line2.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line2} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line3.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line3} options={options300}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line4.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line4} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line5.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line5} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line6.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line6} options={options300}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line7.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line7} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line8.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line8} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line9.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line9} options={options300}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line10.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line10} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line11.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line11} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line12.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line12} options={options300}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line13.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line13} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line14.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line14} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line15.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line15} options={options300}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line16.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line16} options={options100}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line17.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line17} options={options200}/>
                                </>
                            );
                        })
                    }
                    {
                        props.line18.map((value)=>{
                            return (
                                <>
                                    <Polyline path={line18} options={options300}/>
                                </>
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
        </>
    );
}
