import React, { Component } from 'react';
import axios from 'axios';
import Tabledata from './Tabledata';

class Wbnx1 extends Component {
    state={
        data : [],
        result: [],
        isLoading: true,
        errcode: 'Loading...',
        timestamp: [],
    };

    getdata = async () =>{
        console.log("Requesting Data!");
        await axios.get('http://somweb.cf:8000/wbn-x1-r0')
        .then((data) => {
            console.log('Data Check!');
            const mydata = data.data.data;
            return this.setState({data:mydata, isLoading: false});
        }).catch(()=>{
            alert('SERVER ERROR');
            this.setState({errcode:'주임님 서버좀...'});
        })
    }
    gettimer = () =>{
        console.log("Time Start!");

        let time = new Date();
        let years = time.getFullYear();
        let month = time.getMonth();
        let date = time.getDate();
        let hours = time.getHours();
        let minutes = time.getMinutes();
        let seconds = time.getSeconds();

        const result = `Time Stamp : ${years}-${month+1}-${date} (${hours}:${minutes}:${seconds})`;

        return this.setState({result:result});
    }
    getstamp = () =>{
        axios.get('http://somweb.cf:8000/wbn-timestamp').then(response=>{
            const x = response.data.data;
            console.log(x);
            return this.setState({timestamp : x});
        }).catch(()=>{
            alert('SERVER ERROR');
            this.setState({errcode:'주임님 서버좀...'});
        })
    }
    componentDidMount(){
        this.getdata();
        this.gettimer();
        this.getstamp();
    }
    renderbtn = () => {
        this.getdata();
        this.gettimer();
        alert('데이터를 새로고침하였습니다.');
    }
    render() {
        const {data, result,isLoading, errcode, timestamp} = this.state;
        return (
            <div className="table">
                {isLoading ? <div className="loader">
                    <span className="loader__text">{errcode}</span>
                </div> :
                (
                    <div className="content">
                        <div className="table_1">
                            <h1> Network Node : WBN_X1(R0) </h1>
                            {
                                timestamp.map(data=>{
                                    const y = data.timestamp;
                                }),
                                {y}
                            }
                            <span className="spantitle">
                                <button onClick={this.renderbtn}>불러오기</button>
                                <p>{result}</p>
                            </span>
                            <table>
                                <tbody className="table_1_size">
                                    <tr>
                                        <td>Offset Peridod: 5 Mins</td>
                                        <td colSpan="3">BandWidth(Mbps)</td>
                                    </tr>
                                    <tr>
                                        <td>offset</td>
                                        <td>IN</td>
                                        <td>Out</td>
                                        <td>Total</td>
                                    </tr>
                                    {
                                        data.map(data => (
                                            <Tabledata 
                                                offset={data.offset}
                                                speed_in={data.speed_in}
                                                speed_out={data.speed_out}
                                                speed_total={data.speed_total}
                                            />
                                        ))
                                    }
                                </tbody>
                            </table>
                        </div>
                        <div className="chart">
                            <h1>Chart</h1>
                        </div>
                    </div>
                    )
                }
            </div>
        );
    }
}

export default Wbnx1;
