/*global google*/
import React,{ useState } from "react";
import { GoogleMap, InfoWindow, LoadScript, Marker, InfoBox, Polyline} from '@react-google-maps/api';

export default function InfoMap(props){
    const [infomapdata,setInfomapdata] = useState(false);
    const [infopopdata,setInfopopdata] = useState(false);
    const [infotrunkdata,setInfotrunkdata] = useState(false);
    const [activeMarker, setActiveMarker] = useState(null);

    
    /* Data 추출 */
    const line1 = props.line1.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line2 = props.line2.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line3 = props.line3.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line4 = props.line4.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line5 = props.line5.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line6 = props.line6.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line7 = props.line7.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line8 = props.line8.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line9 = props.line9.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line10 = props.line10.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line11 = props.line11.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line12 = props.line12.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line13 = props.line13.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line14 = props.line14.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line15 = props.line15.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line16 = props.line16.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line17 = props.line17.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });
    const line18 = props.line18.map((value)=>{
        return {lat : value.lat,lng : value.lng};
    });

    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px',
        margin : '20px',
    };

    /* google map 첫 화면 */
    const center = {
        lat : -4.39636,
        lng : 15.31693
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };

    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options300 = {
        strokeColor:'blue',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };
    const options200 = {
        strokeColor:'green',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };
    const options100 = {
        strokeColor:'orange',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };
    
    const handleActiveMarker = (marker) => {
        if (marker === activeMarker) {
            return;
        }
        setActiveMarker(marker);
    };

    const handleOnLoad = (map) => {
        const bounds = new google.maps.LatLngBounds();
        props.mapData.forEach(({ position }) => bounds.extend(position));
        map.fitBounds(bounds);
    };
    
    return (
        <>
            <div className="map">
            <div className="btnlist">
                {/* <button className="btntt" onClick={()=>{setInfowWindowpolyline3(!infowWindowpolyline3)}}>Bandwidth 300</button>
                <button className="btntt" onClick={()=>{setInfowWindowpolyline2(!infowWindowpolyline2)}}>Bandwidth 200</button>
                <button className="btntt" onClick={()=>{setInfowWindowpolyline1(!infowWindowpolyline1)}}>Bandwidth 100</button> */}
            </div>
            <div className="container_map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        props.mapData.map((value)=>{
                            console.log(value)
                            return (
                                <>
                                    <Marker 
                                        position={value}
                                        key={value.num}
                                        icon={
                                            {
                                                url : '11.png',
                                                scaledSize : new google.maps.Size(20,25),
                                            }
                                        }
                                        onClick={() => handleActiveMarker(value.num)}
                                    />
                                    {activeMarker === value.num && (
                                        <InfoWindow 
                                            position={{lat : value.lat+0.005100 , lng : value.lng+0.000010}}
                                            onCloseClick={() => setActiveMarker(null)}
                                        >
                                            <div className="infowindow">
                                                <p className="infoitem info_title">NetworkNode Name : {value.nw_node}</p>
                                                <p className="infoitem">lat : {value.lat}</p>
                                                <p className="infoitem">lng : {value.lng}</p>
                                            </div>
                                        </InfoWindow>
                                    )}
                                </>
                            )
                        })
                    }
                    {
                        props.popData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}}
                                        icon={
                                            {
                                                url : '22.png',
                                                scaledSize : new google.maps.Size(25,30),
                                            }
                                        }
                                        onClick={() => handleActiveMarker(value.num)}
                                    />
                                    {activeMarker === value.num && (
                                        <InfoWindow 
                                            position={{lat : value.lat+0.005100 , lng : value.lng+0.000010}}
                                            onCloseClick={() => setActiveMarker(null)}
                                        >
