/*global google*/
import React from "react";
import { GoogleMap,InfoWindow,LoadScript, Marker, InfoBox, Polygon, Polyline} from '@react-google-maps/api';

export default function InfoMap(mapData,popData,trunk,bandWidth,onClickbtn){
    console.log(mapData,onClickbtn)
    /* google map stlye */
    const style = {
        width : '1900px',
        height : '700px'
    };

    /* google map 첫 화면 */
    const center = {
        lat : 37.57055,
        lng : 126.97684
    };

    /* google map icon */
    const icon = {
        url : '3.png',
        scaledSize : new google.maps.Size(20,20),
    };

    /* InfoWindow style */
    const canvas = {
        background : '#fff',
        padding: 10
    };
    /* infoBox options */
    //const options = { closeBoxURL: '', enableEventPropagation: true };
    
    /* Polygon options */
    const options = {
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };

    /* Polygon path */
    const path = [
        {lat: 37.56643, lng: 126.97511},
        {lat: 37.571339, lng: 126.968173},
        {lat: 37.579727, lng: 126.977137},
    ];

    /* Polygon options2 */
    const options2 = {
        strokeColor: 'blue',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        clickable: false,
        draggable: false,
        editable: false,
        visible: true,
        radius: 30000,
        zIndex: 1
    };

    /* Polygon path2 */
    const path2 = [
        {lat: 37.56753, lng: 126.98446},
        {lat: 37.57440, lng: 126.98167},
        {lat: 37.56839, lng: 126.96137},
        {lat: 37.56691, lng: 126.97295}
    ];
    return (
        <>
            <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={15}
                >
                    {
                        mapData.map((value)=>{
                            return (
                                <>
                                    <Marker 
                                        position={{lat : value.lat , lng : value.lng}} 
                                        icon={
                                            {
                                                url : '3.png',
                                                scaledSize : new google.maps.Size(20,20),
                                            }
                                        }
                                        //onMouseOver={mouseEvent}
                                        onMouseOver={(e)=>{
                                            //console.log(e);
                                        }}
                                    />
                                    <Polyline
                                    path={path}
                                    options={options}
                                    />
                                    {/* <InfoBox
                                        options={options}
                                        position={{lat : value.lat , lng : value.lng}}
                                    >
                                    <div className="qwe" style={{ backgroundColor: 'yellow', opacity: 0.75, padding: 12 }}>
                                        <div style={{ fontSize: 16, fontColor: `#08233B` }}>
                                        Hello, World!
                                        </div>
                                    </div>
                                    </InfoBox> */}
                                    {/* {<InfoWindow
                                        position={{lat : value.lat , lng : value.lng}}
                                    >
                                        <div style={canvas}>
                                            <h1>{value.nw_node}</h1>
                                        </div>
                                    </InfoWindow>} */}
                                </>
                            )
                        })
                    }
                    {
                        path2.map((value)=>{
                            return(
                                <>
                                    <Marker position={value}/>
                                    <Polyline path={path2} options={options2}/>
                                </>
                            );
                        })
                    }
                </GoogleMap>
            </LoadScript>
            <button onClick={onClickbtn}>Update</button>
        </div>
        </>
    );
}
