/*global google*/
import React,{ useState,useEffect } from "react";
import { GoogleMap,LoadScript, Marker } from '@react-google-maps/api';
import axios from "axios";

function Map(){

    const [mapData,setMapData] = useState([]);

    useEffect(() => {
        extraction();
    },[]);

    const style = {
        width : '1900px',
        height : '800px'
    };
    const center = {
        lat : 37.55480,
        lng : 126.98297
    };
    const point = [];
    const icon = {
        url : 'O2I_blue.png',
        scaleSize : {width : 10, height : 10},
        //size : new google.maps.Size(40,40),
        //scaledSize: new google.maps.Size(30,30),
    };

    const extraction = async () =>{
        await axios.get('http://somweb.cf:8000/wn-site')
        .then(value=>{
            setMapData(value.data.data);
        })
    };

    //console.log(mapData);
    const Processing = mapData.map((value)=>{
        point.push({lat : value.lat , lng : value.lng})
    });

    return (
        <div className="map">
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap
                    mapContainerStyle={style}
                    center={center}
                    zoom={12}
                >
                    {
                        mapData.map((value)=>{
                            return (
                                <Marker 
                                    position={{lat : value.lat , lng : value.lng}} 
                                    icon={icon}
                                />
                            )
                        })
                    }
                </GoogleMap>
            </LoadScript>
        </div>
    );
}

export default Map;
