/*global google*/
import React,{ useState,useEffect } from "react";
import axios from "axios";
import InfoMap from "./InfoMap";
function Map(){
    
    /* 
    (1) Data 가져오기 v
    (2) Google Map 연결 v
    (3) Data에 있는 좌표 추출 v
    (4) 추출한 좌표에 마커를 생성 v
    (5) 마커 사이즈 변경 v
    (6) 마커에 부가정보 넣기
    (7) 선으로 연결
     */
    const [mapData,setMapData] = useState([]);
    const [popData,setPopData] = useState([]);
    const [trunk,setTrunk] = useState([]);

    useEffect(() => {
        extraction();
        extraction2();
        extraction3();
    },[]);

    /* 데이터 가져오기 */
    const extraction = async () =>{
        await axios.get('http://somweb.cf:8000/map/wn')
        .then(value=>{
            console.log(value.data.data);
            setMapData(value.data.data);
        }).catch(()=>{
            console.log(Error);
        })
    };

    const extraction2 = async () => {
        await axios.get('http://somweb.cf:8000/map/pop')
        .then((value)=>{
            console.log(value.data.data);
            setPopData(value.data.data);
        }).catch(()=>{
            console.log(Error);
        })
    };

    const extraction3 = async () =>{
        await axios.get('http://somweb.cf:8000/map/an')
        .then((value)=>{
            console.log(value.data.data);
            setTrunk(value.data.data);
        }).catch(()=>{
            console.log(Error);
        })
    };
    
    /* 버튼 이벤트 */
    const onClickbtn = () =>{
        extraction();
        extraction2();
        extraction3();
        console.log('Update');
    };

    return (
        <>
            <InfoMap
                mapData={mapData}
                popData={popData}
                trunk={trunk}
            />
            <button onClick={onClickbtn}>Update</button>
        </>
    );
}

export default Map;
