import React from 'react';
import { GoogleMap, LoadScript, Marker, Polyline } from '@react-google-maps/api';

export default function Googlemap({totalLine}) {
    console.log(totalLine);
    const google = window.google;
    const center = { 
        lat : 37.672481, 
        lng : 128.736743
    };
    const zoom = 14;
    const mapSize = {
        width : '1900px',
        height : '700px',
        margin : '20px'
    };
    
    const marker = totalLine.map(value => {
        return (
            <Marker 
                position={value}
                icon={
                    {
                        url : "nodepop.png",
                        scaledSize: new window.google.maps.Size(25,25)
                    }
                }
            />
        )
    });
    const ifmarker = totalLine.map(value=>{
        if(value.wn_node){
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "battery25.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
                />
            )
        }else if(value.p_node){
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "battery50.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
            />
            )
        }else {
            return (
                <Marker 
                    position={value}
                    icon={
                        {
                            url : "battery75.png",
                            scaledSize: new window.google.maps.Size(20,40)
                        }
                    }
                />
            )
        }
    });

    const polyLine = totalLine.sort((a,b)=>{
        return a.line - b.line;
    });
    
    const options400 = {
        strokeColor:'yellowgreen',
        strokeOpacity: 0.75,
        strokeWeight: 8,
    };
    const options300 = {
        strokeColor:'gold',
        strokeOpacity: 0.75,
        strokeWeight: 6,
    };
    const options200 = {
        strokeColor:'gray',
        strokeOpacity: 0.75,
        strokeWeight: 4,
    };
    const options100 = {
        strokeColor:'brown',
        strokeOpacity: 0.75,
        strokeWeight: 2,
    };

    return (
        <div>
            <LoadScript googleMapsApiKey="AIzaSyDevvetng60XedeOqk-qW9TF-XBNQsyxdE">
                <GoogleMap center={center} zoom={zoom} mapContainerStyle={mapSize}>
                    {totalLine && ifmarker}
                    {polyLine.map((value,index,arr)=>{
                        console.log(value.bw)
                        return (
                            <Polyline 
                                path={arr}
                                options={value.bw === 400 ? options400 : value.bw === 300 ? options300 : value.bw === 200 ? options200 : options100}
                            />
                        );
                    })}
                </GoogleMap>
            </LoadScript>
        </div>
    )
}
